INSERT INTO `robot_questions` VALUES (4,'no name',NULL,'<h2>How To Pick a Lock!</h2>\n<style type=\"text/css\">\n.scroller\n{\n	height: 108px;\n	width: 247px;\n	overflow-y: hidden;\n}\n.digit\n{\n	display: inline-block;\n	position: relative;	\n}\n.number\n{\n	font-size: 100px;\n	font-family: \"Arial Rounded MT\", Arial, Helvetica, sans-serif;\n	font-weight: bold;\n	color: lightgrey;\n	margin: 0px 0px 90px 0px;\n	border: 0px solid white;\n	padding: 0px;\n}\n</style>\n\n<script type=\"text/javascript\">\n	var number = \"0000\"\n	function change_number(i , num)\n	{\n		$($(\'.digit\')[i]).animate({\n		 top: -108*num\n		}, 80);\n	}\n	function scroll(target)\n	{\n		target = target.toString();\n		for(i=0 ; i<4-target.length ; i++)\n			change_number(i, 0);\n		for(i=target.length-1 ; i>=0 ; i--)\n		{\n			num = parseInt(number[i]);\n			while(num != parseInt(target[i]))\n			{				\n				num++;\n				if(num == 10)\n					num = 0;\n				change_number((4-target.length)+i, num);\n			}\n			number[i] = number.toString();\n		}\n	}\n</script>\n\n<center>\n<div class=\"scroller\">\n	<div class=\"digit\">\n		<p>#</p>\n		<p class=\"number\">0</p>\n		<p class=\"number\">1</p>\n		<p class=\"number\">2</p>\n		<p class=\"number\">3</p>\n		<p class=\"number\">4</p>\n		<p class=\"number\">5</p>\n		<p class=\"number\">6</p>\n		<p class=\"number\">7</p>\n		<p class=\"number\">8</p>\n		<p class=\"number\">9</p>\n	</div>\n	<div class=\"digit\">\n		<p>#</p>\n		<p class=\"number\">0</p>\n		<p class=\"number\">1</p>\n		<p class=\"number\">2</p>\n		<p class=\"number\">3</p>\n		<p class=\"number\">4</p>\n		<p class=\"number\">5</p>\n		<p class=\"number\">6</p>\n		<p class=\"number\">7</p>\n		<p class=\"number\">8</p>\n		<p class=\"number\">9</p>\n	</div>\n	<div class=\"digit\">\n		<p>#</p>\n		<p class=\"number\">0</p>\n		<p class=\"number\">1</p>\n		<p class=\"number\">2</p>\n		<p class=\"number\">3</p>\n		<p class=\"number\">4</p>\n		<p class=\"number\">5</p>\n		<p class=\"number\">6</p>\n		<p class=\"number\">7</p>\n		<p class=\"number\">8</p>\n		<p class=\"number\">9</p>\n	</div>\n	<div class=\"digit\">\n		<p>#</p>\n		<p class=\"number\">0</p>\n		<p class=\"number\">1</p>\n		<p class=\"number\">2</p>\n		<p class=\"number\">3</p>\n		<p class=\"number\">4</p>\n		<p class=\"number\">5</p>\n		<p class=\"number\">6</p>\n		<p class=\"number\">7</p>\n		<p class=\"number\">8</p>\n		<p class=\"number\">9</p>\n	</div>\n</div>\n</center>\n<div id=\"question_code\" style=\"height: 237px;\">import java.util.Scanner;\nclass LockOpener\n{	\n	public static void main(String[] args) \n	{\n		Scanner input = new Scanner(System.in);\n		int password = 4567;\n		System.out.println(password);\n	}\n}\n</div>\n<p style=\"font-size: 15px\">\n<b>Copy and Paste</b> the code, and click run and see what happens!<br>\nTo open the next level, print the secret code, <b>6789</b>\n</p>',1,'BootCamp',1,'2013-02-14 17:40:47','2013-03-10 08:50:12','lib/Robots/BootCamp1.rb','6789'),(14,'no name',NULL,'<h2>Trying All Possible Passwords</h2>\n<style type=\"text/css\">\n.scroller\n{\n	height: 108px;\n	width: 247px;\n	overflow-y: hidden;\n}\n.digit\n{\n	display: inline-block;\n	position: relative;	\n}\n.number\n{\n	font-size: 100px;\n	font-family: \"Arial Rounded MT\", Arial, Helvetica, sans-serif;\n	font-weight: bold;\n	color: lightgrey;\n	margin: 0px 0px 90px 0px;\n	border: 0px solid white;\n	padding: 0px;\n}\n</style>\n\n<script type=\"text/javascript\">\n	var number = \"0000\"\n	function change_number(i , num)\n	{\n		$($(\'.digit\')[i]).animate({\n		 top: -108*num\n		}, 80);\n	}\n	function scroll(target)\n	{\n		target = target.toString();\n		for(i=0 ; i<4-target.length ; i++)\n			change_number(i, 0);\n		for(i=target.length-1 ; i>=0 ; i--)\n		{\n			num = parseInt(number[i]);\n			while(num != parseInt(target[i]))\n			{				\n				num++;\n				if(num == 10)\n					num = 0;\n				change_number((4-target.length)+i, num);\n			}\n			number[i] = number.toString();\n		}\n	}\n</script>\n\n<center>\n<div class=\"scroller